import * as React from "react";
import { styled } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Unstable_Grid2";
import { Typography } from "@mui/material";

const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: "center",
    color: theme.palette.text.secondary,
}));

export default function ScheduleView() {
    const Consultants = ["Alan", "Brad", "Charlene"];

    const daysOfTheWeek = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];

    const Schedule = [
        {
            id: 1,
            person: "Alan Au",
            tasks: [
                { type: "work", effort: 5 },
                { type: "free", effort: 2 },
            ],
        },
        {
            id: 2,
            person: "Bob Bennington",
            tasks: [
                { type: "work", effort: 2 },
                { type: "free", effort: 5 },
            ],
        },
    ];

    return (
        <Box sx={{ flexGrow: 1, m: 2 }} color="secondary">
            {/*Days of the week*/}
            <Grid container spacing={0.5} columns="9">
                <Grid xs={2}>
                    <Typography align="center">Consultants</Typography>
                </Grid>
                {daysOfTheWeek.map((day) => (
                    <Grid xs={1}>
                        <Typography variant="h6" align="center">
                            {day}
                        </Typography>
                    </Grid>
                ))}
            </Grid>
            {/* */}
            <Grid container spacing={0.5} columns="9">
                {Schedule.map((sched) => (
                    <>
                        <Grid xs={2} key={sched.id}>
                            <Button variant="contained" fullWidth>
                                {sched.person}
                            </Button>
                        </Grid>
                        {sched.tasks.map((task) => (
                            <Grid xs={task.effort}>
                                <Button variant="contained" fullWidth>
                                    {task.type}
                                </Button>
                            </Grid>
                        ))}
                    </>
                ))}
            </Grid>
            <Grid container spacing={0.5} columns="7">
                <Grid xs={5}>
                    <Button variant="contained" fullWidth>
                        PRJ-2022-1
                    </Button>
                </Grid>
                <Grid xs={4}>
                    <Item>xs=4</Item>
                </Grid>
                <Grid xs={4}>
                    <Item>xs=4</Item>
                </Grid>
                <Grid xs={8}>
                    <Item>xs=8</Item>
                </Grid>
            </Grid>
        </Box>
    );
}
