import React from "react";
import { useForm } from "react-hook-form";

const LoginForm = () => {
    const { register, handleSubmit, errors } = useForm();

    const onSubmit = (data) => {
        console.log("Login button clicked! Data:", data);
    };

    return (
        <form className="form-group" id="loginFormID">
            <label htmlFor="emailAddressField" className="form-label">
                Email address
            </label>
            <input
                type="email"
                placeholder="email@domain.com"
                className="form-control"
                id="emailAddressField"
                aria-describedby="emailHelp"
                /* ref={register} */
            />

            <label htmlFor="passwordField" className="form-label">
                Password
            </label>
            <input
                type="password"
                className="form-control"
                id="passwordField"
                aria-describedby="passwordField"
                /* ref={register} */
            />

            <button
                onClick={handleSubmit(onSubmit)}
                className="btn btn-primary"
                type="submit"
                htmlFor="loginFormID"
            >
                Log me in!
            </button>
        </form>
    );
};

export default LoginForm;
