import React, { Component } from "react";
import { getScheduledItems } from "../mockServices/schedule";
import { eachDayOfInterval, format } from "date-fns";

class DayCalendar extends Component {
    state = { scheduleItems: getScheduledItems() };
    render() {
        const { displayStartDate, displayEndDate } = this.props;

        return (
            <React.Fragment>
                <table className="table table-dark">
                    <tr>
                        {this.populateDays(displayStartDate, displayEndDate)}
                    </tr>
                </table>
            </React.Fragment>
        );
    }

    populateDays(startDate, endDate) {
        //const numOfDays = (endDate - startDate) / (1000 * 3600 * 24);
        const days = eachDayOfInterval({ start: startDate, end: endDate });
        days.map((day) => console.log(format(day, "dd-MM-yyyy")));
    }
}

export default DayCalendar;
